mapper.select:
SELECT USER_EMAIL
FROM USER_INFO
WHERE USER_EMAIL = #{user_email}

select *
from USER_INFO
where USER_ID = #{user_id} and USER_PW = #{user_pw}

SELECT USER_NAME,USER_EMAIL,USER_ID
FROM USER_INFO
WHERE USER_NAME= #{user_name} AND USER_EMAIL= #{user_email}

mapper.update:
UPDATE USER_INFO
SET USER_PW = #{user_pw}
WHERE USER_ID = #{user_id} and USER_EMAIL = #{user_email}

update USER_INFO
set USER_ID = #{user_id}, USER_EMAIL = #{user_email}, USER_NAME = #{user_name}
where USER_SEQ = #{user_seq}

mapper.select:
select user_name
from USER_INFO
where USER_SEQ = #{user_seq}
