mapper.select:
SELECT DECODE(COUNT(1), 0, 'N', 'Y') AS EXISTS_YN
FROM USER_INFO
WHERE USER_EMAIL = #{user_email}

select *
from USER_INFO
where USER_ID = #{user_id} and USER_PW = #{user_pw}

SELECT USER_NAME,USER_EMAIL,USER_ID
FROM USER_INFO
WHERE USER_NAME= #{user_name} AND USER_EMAIL= #{user_email}

mapper.update:
UPDATE USER_INFO
SET USER_PW = #{user_pw}
WHERE USER_SEQ = #{user_seq} OR USER_EMAIL = #{user_email}
